(New-Object System.Net.WebClient).DownloadFile("https://github.com/treyhay31/posh-scaffolding/archive/master.zip", "$($env:UserProfile)\Downloads\module.zip"); $env:PSModulePath -Split ";" | ForEach-Object { Expand-Archive -Path "$($env:UserProfile)\Downloads\module.zip" -Destination "$_\" -Force; Get-ChildItem "$_\posh-scaffolding-master"; Remove-Item "$_\posh-scaffolding-master" -Recurse -Force; Get-ChildItem $_ }